mapper.select:
select id , title , category , gmt_create , gmt_update , summary, tags , auth
from article


        id = #{ id }


        and category = #{ category }



        and title LIKE #{ title2 }



        and markdown LIKE #{ markdown2 }


        and #{tags} in (
        select SUBSTRING_INDEX(
        SUBSTRING_INDEX(tags,'|',help_topic_id+1),'|',-1
        ) AS num 
        from mysql.help_topic 
        where help_topic_id < LENGTH(tags)-LENGTH(REPLACE(tags,'|',''))+1)


        and auth <= #{ auth }


order by gmt_update desc

select id , title , category , gmt_create , gmt_update , summary, tags , auth
from article


        id = #{ id }



        or category LIKE #{ category2 }



        or title LIKE #{ title2 }



        or markdown LIKE #{ markdown2 }


        or #{tags} in (
        select SUBSTRING_INDEX(
        SUBSTRING_INDEX(tags,'|',help_topic_id+1),'|',-1
        ) AS num 
        from mysql.help_topic 
        where help_topic_id < LENGTH(tags)-LENGTH(REPLACE(tags,'|',''))+1)


        and auth <= #{ auth }


order by gmt_update desc

mapper.update:
update article
set title=#{ title }, auth=#{auth}, category = #{ category }, summary = #{ summary }, tags = #{ tags }, markdown = #{ markdown }, gmt_update = #{ gmtUpdate }
where id = #{ id }
